Title_Int:	dc.b	"SONIC THE             HEDGEHOG 3                "
ROMSerial:	dc.b	"GM 00001079-00"
Checksum:	dc.w	$1019
IOSupport:	dc.b	"J               "
ROMStartLoc:	dc.l	StartOfRom
ROMEndLoc:	dc.l	EndOfRom-1
